tinymce.PluginManager.add('tateyoko', function(editor, url) {
  var tateyoko = function () {
        var selectClass = editor.dom.select("body") 
      editor.dom.toggleClass(editor.dom.select('body'), 'vrtl' );
      editor.dom.toggleClass(editor.dom.select("body"), "hltr");
      console.log("縦書き横書き切り替え")

}

  /* Add a button that opens a window */
  editor.ui.registry.addButton('tateyoko', {
    text:'縦書',
    onAction: function () {
      /* Open window */
      tateyoko();
    }
  });
  /* Adds a menu item, which can then be included in any menu via the menu/menubar configuration */
  editor.ui.registry.addMenuItem('tateyoko', {
    text: '縦書',
    onAction: function() {
      /* Open window */
      tateyoko();
    }
  });
  /* Return the metadata for the help plugin */
  return {
    getMetadata: function () {
      return  {
        name: '縦書',
        url: 'https://99nyorituryo.hatenablog.com/'
      };
    }
  };
});